import React from "react";
import "../css/experience.css";


class LayerIframe extends React.Component {
  render() {
    return (
      <div className='popup'>
        <div className='popup_inner'>
          <h1>{this.props.text}</h1>
        <button onClick={this.props.closePopup}>close me</button>
        </div>
      </div>
    );
  }
}
class Blog extends React.Component {
  constructor() {
    super();
    this.state = {
      showPopup: false
    };
  }
  togglePopup() {
    this.setState({
      showPopup: !this.state.showPopup
    });
  }
  render() {
    return (
      <div className="container-wrap blog">
        <h2 className="txt-headline text-slate-900 dark:text-slate-200">Blog</h2>
        <div className="contents-wrap">
          <div className="aaa">
            <a href="#" onClick={this.togglePopup.bind(this)}>레이어 열기</a>

          </div>
        </div>
      </div>
    );
  }
};



ReactDOM.render(
  <App />,
  document.getElementById('content')
);
