  handleClick(event) {
    this.setState(prevState => ({
      isToggleOn: !prevState.isToggleOn
    }));

    window.location.hash = $(this).attr('rel');
    event.preventDefault();
  }

  render() {
    return (
      <div className="container-wrap blog">
        <h2 className="txt-headline text-slate-900 dark:text-slate-200">Blog</h2>
        <div className="contents-wrap">
          <a class="porlink" href="portfolio/kakaopage2.html" rel="#kakaopage2">열기</a>
          <Item.Group items={DataPlusx} className="porlink" />


          <div className="aaa">
            <button onClick={this.togglePopup.bind(this)}>show popup</button>
            {this.state.showPopup ? 
              <LayerIframe text='Close Me' closePopup={this.togglePopup.bind(this)} />
              : null
            }
          </div>
        </div>
        <div className="por_detail"></div>
      </div>
    );
  }
};


export default Blog;


